import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import StarIcon from '@material-ui/icons/Star';
import Divider from '@material-ui/core/Divider';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import _ from 'lodash';

const styles = theme => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    position: 'relative',
  },
  container: {
    position: 'relative',
    marginTop: '50px',
    width: '60%',
    left: '50%',
    transform: 'translateX(-50%)',
  },
  catalog: {
    display:'flex',
    justifyContent: 'space-between',
  }
});

class SimpleTabs extends React.Component {
  state = {
    anchorEl: null,
    total_count: 2,
    repo: [
      {
        full_name: 'EZcook',
        description: 'Forked from tony6847310/Knowledge-Keeper',
        updated_at: 'Nov 17, 2017',
        language: 'Javascript',
        stargazers_count: '1',
      },
      {
        full_name: 'EZcook2',
        description: 'Forked from tony6847310/Knowledge-Keeper',
        updated_at: 'Nov 17, 2017',
        language: 'Javascript',
        stargazers_count: '1',
      }
    ],
  };

  handleChange = (event, value) => {
    this.setState({ value });
  };

  handleClick = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const { classes } = this.props;
    const { value } = this.state;
    const { anchorEl } = this.state;

    return (
      <div className={classes.root}>
        <div className={classes.container}>
          <Typography 
            component="h2" 
            variant="h1" 
            gutterBottom
          >
            GitHub Repo Finder
          </Typography>
          <TextField
            id="outlined-full-width"
            style={{ margin: 8 }}
            placeholder="Search Repo"
            fullWidth
            margin="normal"
            variant="outlined"
            InputLabelProps={{
              shrink: true,
            }}
          />
          <List className={classes.root}>
            <ListItem className={classes.catalog}>
              <Typography
                variant="h6" 
                gutterBottom
              >
                {this.state.total_count} repository results
              </Typography>
              <Button
                aria-owns={anchorEl ? 'simple-menu' : undefined}
                aria-haspopup="true"
                onClick={this.handleClick}
                className={classes.sortBtn}
              >
                Sort: Best Match
              </Button>
              <Menu id="simple-menu" anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={this.handleClose}>
                <MenuItem onClick={this.handleClose}>Stars</MenuItem>
                <MenuItem onClick={this.handleClose}>Forks</MenuItem>
                <MenuItem onClick={this.handleClose}>Best Match</MenuItem>
              </Menu>
            </ListItem>
            <Divider component="li" />
            <ListItem >
              <ListItemText primary={this.state.repo[0].full_name} secondary={"Updated: " + this.state.repo[0].updated_at} />
              <ListItemText secondary={this.state.repo[0].description} />
              <ListItemText primary={this.state.repo[0].language} />
              <ListItemIcon>
                <StarIcon />
                <ListItemText primary={this.state.repo[0].stargazers_count} />
              </ListItemIcon>
            </ListItem>
          </List>
        </div>
      </div>
    );
  }
}

SimpleTabs.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleTabs);
